INSERT INTO `berita` (`id`, `photo_url`, `video_url`, `judul`, `ringkasan`, `konten`, `waktu_terbit`, `waktu_berakhir`, `id_kategori`, `status`, `create_user`, `create_date`, `edit_user`, `edit_date`, `is_deleted`) VALUES ('1', 'https://alazhar-asset.oss-ap-southeast-5.aliyuncs.com/berita/saebx.jpg', 'https://www.youtube.com/watch?v=VrPUpwcb2qM', 'Salam Al Azhar Versi 3 hadir dengan Keunggulan Terbaru', '<p>Salam Al Azhar merupakan platform resmi yang dihadirkan oleh Yayasan Pesantren Islam Al Azhar untuk memudahkan aktivitas pendidikan anak Anda. Pastikan mendownload melalui penyedia resmi bertulisan \"Yayasan Pesantren Islam AL Azhar\".</p>\n<p>&nbsp;</p>', '<p>Salam Al Azhar merupakan platform resmi yang dihadirkan oleh Yayasan Pesantren Islam Al Azhar untuk memudahkan aktivitas pendidikan anak Anda. Pastikan mendownload melalui penyedia resmi bertulisan \"Yayasan Pesantren Islam AL Azhar\".</p>\n<p>Salam Al Azhar dirancang untuk memudahkan aktivits layanan yang terbaik untuk peserta didik.</p>\n<p><strong>Fitur Unggulan</strong></p>\n<p>1. Menyajikan informasi kegiatan sekolah dan komunikasi antara pengguna (Orang Tua Murid) dan penyedia layanan.</p>\n<p>2. Penerimaan murid baru setiap gelombang diproses secara real time dan dapat langsung tanpa manual dokumen.</p>\n<p>3.&nbsp;Mengubah data profil murid langsung di aplikasi mobile.</p>\n<p>4.&nbsp;Bayar Uang sekolah ke rekening YPI Al Azhar dengan aman melalui berbagai channel pembayaran online syariah.</p>\n<p>5.&nbsp;Dapatkan informasi kegiatan belajar mengajar seperti seperti jadwal belajar, kartu ujian, nilai, data kehadiran, dll.</p>\n<p>6.&nbsp;Salam Al Azhar dapat memberikan notifikasi/informasi melalui SMS, email dan mobile untuk semua informasi pembayaran dan informasi penting akademis lainnya.</p>', '2019-07-08 05:48:13', '2020-01-01 05:48:13', '1', 'ACTIVE', 'admin:1', '2019-07-08 20:10:40', 'admin:1', '2019-07-20 13:35:45', '0'),
